import React from 'react'

class Prices extends React.Component {
    state = {
        currency: 'USD'
    }

    render() {
        let list = "";
        if (this.state.curreny === 'USD') {
            list = <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.USD.description}
            : <span class="badge badge-primary">
                {this.props.bpi.USD.code}
            </span> <strong>{this.props.bpi.USD.rate}</strong>
        </li>
        } else if (this.state.curreny === 'GBP') {
            list = <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.GBP.description}
            : <span class="badge badge-primary">
                {this.props.bpi.GBP.code}
            </span> <strong>{this.props.bpi.USD.rate}</strong>
        </li>
        } else if (this.state.curreny === 'EUR') {
            list = <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.EUR.description}
            : <span class="badge badge-primary">
                {this.props.bpi.EUR.code}
            </span> <strong>{this.props.bpi.EUR.rate}</strong>
        </li>
        }
       return (
           <div>
               <ul className="list-group">
                   <li className="list-group-item">
                       Bitcoin rate for {this.props.bpi.USD.description}
                       : <span class="badge badge-primary">
                           {this.props.bpi.USD.code}
                       </span> <strong>{this.props.bpi.USD.rate}</strong>
                   </li>
               </ul>
           </div>
       )
    }
}

export default Prices;
